import PropTypes from "prop-types";
import React from "react";
import Link from "next/link";
import { Input, Menu, Row, Col } from "antd";
//Row가로줄 Col 세로줄

const AppLayout = ({ children }) => {
  return (
    <div>
      <div>
        <Menu mode="horizontal">
          <Menu.Item>
            <Link href="/">
              <a>노드버드</a>
            </Link>
          </Menu.Item>
          <Menu.Item>
            <Link href="/profile">
              <a>프로필</a>
            </Link>
          </Menu.Item>
          <Menu.Item>
            <Input.Search enterButton style={{ verticalAlign: "middle" }} />
          </Menu.Item>
          <Menu.Item>
            <Link href="/signup">
              <a>회원가입</a>
            </Link>
          </Menu.Item>
        </Menu>
        <Row>
          {/* 24칸 //  24칸중에 6칸차지한다 // 24칸이 넘어가면  다음줄로 넘어간다   */}
          <Col xs={24} md={6} />
          왼쪽메뉴
          <Col xs={24} md={12} />
          {children}
          <Col xs={24} md={6} />
          오른쪽 메뉴
        </Row>
      </div>
    </div>
  );
};

AppLayout.propTypes = {
  children: PropTypes.node.isRequired,
};

export default AppLayout;
